
public class MultiDimension {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[][] arr; //first bracket for row, second for column
		arr = new int[5][];//specifying rows = 5
		//Adding Columns = 4
		for(int i=0 ; i<arr.length ; i++) {
			arr[i] = new int[4];
		}

	}

}
